import React from 'react';
import Tablexml from './Tablexml';
import { parseString } from 'xml2js';

class Pubmed extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoaded: false,
        };
    }

    componentDidMount() {
        fetch('http://localhost:9000/pubmed')
            .then(res => res.text())
            .then(data => {
                this.setState({
                    isLoaded: true,
                    items: data,
                })
            });
    }

    removeWhitespace = function (xml) {
        console.log(xml, 'xml');
        var loopIndex;
        for (loopIndex = 0; loopIndex < xml.childNodes.length; loopIndex++) {
            var currentNode = xml.childNodes[loopIndex];
            if (currentNode.nodeType == 1) {
                this.removeWhitespace(currentNode);
            }
            if (!(/\S/.test(currentNode.nodeValue)) && (currentNode.nodeType == 3)) {
                xml.removeChild(xml.childNodes[loopIndex--]);
            }
        }
    }

    addTableRowsFromXmlDoc = function (xmlNodes, tableNode) {
        var theTable = tableNode.parentNode;
        var newRow, newCell, i, j;
        console.log("Number of nodes: " + xmlNodes.length);
        for (i = 0; i < xmlNodes.length; i++) {
            newRow = tableNode.insertRow(i);
            newRow.className = (i % 2) ? "OddRow" : "EvenRow";
            for (j = 0; j < xmlNodes[i].childNodes.length; j++) {
                newCell = newRow.insertCell(newRow.cells.length);
                if (xmlNodes[i].childNodes[j].firstChild) {
                    newCell.innerHTML = xmlNodes[i].childNodes[j].firstChild.nodeValue;
                } else {
                    newCell.innerHTML = "-";
                }
                console.log("cell: " + newCell);
            }
        }
        theTable.appendChild(tableNode);
    }

    showResult = function (xmlDoc) {
        this.removeWhitespace(xmlDoc);
        var outputResult = document.getElementById("BodyRows");
        var rowData = xmlDoc.getElementsByTagName("Item");

        this.addTableRowsFromXmlDoc(rowData, outputResult);
    }

    render() {

        var isLoaded = this.state.isLoaded;
        var items = this.state.items;
        this.showResult(items);
        console.log(items, 'items');

        if (!isLoaded) {
            return <div>Loading...</div>;
        }

        else {
            return (
                <table id="MainTable">
                    <tbody id="BodyRows"></tbody>
                </table>
                // <div className="App">
                //     <Tablexml data={items} />
                // </div>
            )

        }

    }
}
export default Pubmed;
