import React from 'react';

const getKeys = function (products) {
    return Object.keys(products[0]);
}

const getHeader = function (products) {
    var keys = getKeys(products);
    return keys.map((key, index) => {
        return <th onClick={e => this.onSort(e, { key })} key={key}>{key.toUpperCase()}</th>
    })
}

const getRowsData = function (products) {
    var items = products;
    var keys = getKeys(products);
    return items.map((row, index) => {
        return <tr key={index}><RenderRow key={index} data={row} keys={keys} /></tr>
    })
}

const RenderRow = (props) => {
    return props.keys.map((key, index) => {
        return <td key={props.data[key]}>{props.data[key]}</td>
    })
}

const Table = (props) => {

    let products = props.data;
    const [sortedField, setSortedField] = React.useState(null);
    let sortedProducts = [...products];
    sortedProducts.sort((a, b) => {
        if (a.name < b.name) {
            return -1;
        }
        if (a.name > b.name) {
            return 1;
        }
        return 0;
    });

    return (
        <div>
            <table>
                <thead>
                    <tr>{getHeader(products)}</tr>
                </thead>
                <tbody>
                    {getRowsData(products)}
                </tbody>
            </table>
        </div>

    );
}




export default Table;
