import React from 'react';



const RenderRow = (props) => {
    return props.keys.map((key, index) => {
        return <td key={props.data[key]}>{props.data[key]}</td>
    })
}

const useSortableData = (items, config = null) => {
    const [sortConfig, setSortConfig] = React.useState(config);

    const sortedItems = React.useMemo(() => {
        let sortableItems = [...items];
        if (sortConfig !== null) {
            sortableItems.sort((a, b) => {
                if (a[sortConfig.key] < b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? -1 : 1;
                }
                if (a[sortConfig.key] > b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? 1 : -1;
                }
                return 0;
            });
        }
        return sortableItems;
    }, [items, sortConfig]);

    const requestSort = (key) => {
        let direction = 'ascending';
        if (
            sortConfig &&
            sortConfig.key === key &&
            sortConfig.direction === 'ascending'
        ) {
            direction = 'descending';
        }
        setSortConfig({ key, direction });
    };

    return { items: sortedItems, requestSort, sortConfig };
};

const Table = (props) => {

    let products = props.data;
    const { items, requestSort, sortConfig } = useSortableData(products);
    const getClassNamesFor = (name) => {
        if (!sortConfig) {
            return;
        }
        console.log(name, 'name');
        return sortConfig.key === name ? sortConfig.direction : undefined;
    };

    const getKeys = function (products) {
        return Object.keys(products[0]);
    }

    const getHeader = function (products) {
        var keys = getKeys(products);
        return keys.map((key, index) => {
            return <th onClick={() => requestSort({ key })} className={getClassNamesFor({ key })} key={key}>{key.toUpperCase()}</th>
        })
    }

    const getRowsData = function (products) {
        var items = products;
        var keys = getKeys(products);
        return items.map((row, index) => {
            return <tr key={index}><RenderRow key={index} data={row} keys={keys} /></tr>
        })
    }

    return (
        <div>
            <table>
                <thead>
                    <tr>{getHeader(items)}</tr>
                </thead>
                <tbody>
                    {getRowsData(items)}
                </tbody>
            </table>
        </div>

    );

}




export default Table;
